import React, { useState, useEffect } from "react";
import Home from "./Home";
import OnboardingSlider from "./OnboardingSlider";
import firebase from 'firebase/app'; // Import firebase without default export
import firebaseConfig from "./firebaseConfig";
import "firebase/firestore"; // Import other Firebase services if needed

// Initialize Firebase
if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
}

export default function App() {
  const [realApp, showRealApp] = useState(true);
  const showRealAppFn = () => {
    showRealApp(!realApp);
  };

  if (realApp) {
    return <Home showRealAppFn={showRealAppFn} />;
  } else {
    return <OnboardingSlider showRealAppFn={showRealAppFn} />;
  }
}
